// public/service-worker.js

// Load Workbox from a CDN using importScripts()
importScripts('https://storage.googleapis.com/workbox-cdn/releases/6.5.4/workbox-sw.js');

if (workbox) {
  console.log('Workbox is loaded');

  // Precache assets generated by your build (if using a build tool that injects __WB_MANIFEST)
  workbox.precaching.precacheAndRoute(self.__WB_MANIFEST || []);

  // Cache Cloudinary API responses
  workbox.routing.registerRoute(
    ({ url }) => url.origin === 'https://api.cloudinary.com',
    new workbox.strategies.StaleWhileRevalidate({
      cacheName: 'cloudinary-cache',
    })
  );
} else {
  console.log("Workbox didn't load");
}

// Listen for skipWaiting message (optional)
self.addEventListener('message', (event) => {
  if (event.data && event.data.type === 'SKIP_WAITING') {
    self.skipWaiting();
  }
});
